CREATE TABLE IF NOT EXISTS `blog` (
  `id` int(255) NOT NULL AUTO_INCREMENT,
  `uid` int(255) NOT NULL,
  `uip` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `bid` int(255) NOT NULL COMMENT 'Parent blog',
  `title` varchar(2000) COLLATE utf8_unicode_ci NOT NULL,
  `link` varchar(3000) COLLATE utf8_unicode_ci NOT NULL,
  `content` longtext COLLATE utf8_unicode_ci NOT NULL,
  `thumb` varchar(2000) COLLATE utf8_unicode_ci NOT NULL,
  `tagged` longtext COLLATE utf8_unicode_ci NOT NULL COMMENT 'Tagged uid',
  `tid` longtext COLLATE utf8_unicode_ci NOT NULL COMMENT 'Trends id',
  `cid` int(255) NOT NULL COMMENT 'Category id',
  `views` int(255) NOT NULL,
  `likes` longtext COLLATE utf8_unicode_ci NOT NULL,
  `dislikes` longtext COLLATE utf8_unicode_ci NOT NULL,
  `fav` longtext COLLATE utf8_unicode_ci NOT NULL,
  `time` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `last_updated` char(15) COLLATE utf8_unicode_ci NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci AUTO_INCREMENT=26 ;

